import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
// import './index.css';

const App = () => {
  const[movies, setMovies] = useState([]);
  const[search, setSearch] = useState('');
  const[year, setYear] = useState('');


  const y = (new Date()).getFullYear();
  const years = Array.from(new Array(25), (val, index) => y-index);

const getMovies = async (page=1,year='') => {
  try{ 
    let moviesRequest; 
    if(search.length>2){
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=${search}&page=${page}&y=${year}&apikey=d0108237`
    )}
    else {
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=Iron&apikey=d0108237`
    )}
    console.log(moviesRequest.data.Search)
    setMovies(moviesRequest.data.Search)
  }
  catch (error){
    console.log(error)
  }
}

const filterYear = (e) => {
  setYear(e.target.value)
}
useEffect(() => {
  getMovies()
},[])

  return (
    <div>
      <input type="text" onChange={(e)=>{setSearch(e.target.value)}} />
      <button onClick={(() => {getMovies(search)})}> Search </button>
      <button onClick={(() => {getMovies(1,year)})}> Search </button>
      <select onChange={filterYear}>
        <option value="" hidden >Year</option> 
        {
          years.map((year, index) => {
            return <option key={`year${index}`} value={year}>{year}</option>
          })
        }
      </select>
      {movies.length > 0 ? movies.map(m => {
        return (
          <div>
            <img src={m.Poster} alt="" />
            <p>{m.Title}</p>
          </div>
        )
      }): ''}
    </div>
  )
}

ReactDOM.render(<App/>, document.getElementById('root'));
