import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
// import './index.css';

const App = () => {
  const[movies, setMovies] = useState([]);
  const[search, setSearch] = useState('');

const getMovies = async () => {
  let moviesRequest
  moviesRequest = await axios.get(
    'http://www.omdbapi.com/?s=Iron&apikey=d0108237'
  );
    setMovies(moviesRequest.data.Search);
    moviesRequest = await axios.get(
      'http://www.omdbapi.com/?s=$&apikey=d0108237'
    );
      setMovies(moviesRequest.data.Search);

}

useEffect(() => {
  getMovies();
},[]);

useEffect(() => {
  getMovies();
}, [search])

  return (
    <div>
      <input type="text" onChange={(e)=>{setSearch(e.target.value)}} /> Search
      {movies.length > 0 ? movies.map(m => {
        return (
          <div>
            <img src={m.Poster} alt="" />
            <p>{m.Title}</p>
          </div>
        )
      }): ''}
    </div>
  )
}

ReactDOM.render(<App/>, document.getElementById('root'));
