import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
// import './index.css';

const App = () => {
  const[movies, setMovies] = useState([]);
  const[search, setSearch] = useState('');
  const[yea, setYear] = useState('');

const getMovies = async () => {
  try{ 
    let moviesRequest; 
    if(search.length>2){
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=${search}&apikey=d0108237`
    )}
    else {
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=Iron&apikey=d0108237`
    )}
    console.log(moviesRequest.data.Search)
    setMovies(moviesRequest.data.Search)
  }
  catch (error){
    console.log(error)
  }
}

useEffect(() => {
  getMovies()
},[])

  return (
    <div>
      <input type="text" onChange={(e)=>{setSearch(e.target.value)}} />
      <button onClick={(() => {getMovies(search)})}> Search </button>
      {movies.length > 0 ? movies.map(m => {
        return (
          <div>
            <img src={m.Poster} alt="" />
            <p>{m.Title}</p>
            <button> Like </button>
          </div>
        )
      }): ''}
    </div>
  )
}

ReactDOM.render(<App/>, document.getElementById('root'));
