// import React, {useEffect, useState} from 'react';
// import ReactDOM from 'react-dom';
// import axios from 'axios';
// import {v4 as uuid} from 'uuid';
// // import './index.css';

const App = () => {
  const[movies, setMovies] = useState([]);
  const[search, setSearch] = useState('');
  const[year, setYear] = useState('');
  const[showmovies,setShowemovies] = useState(movies)


  const y = (new Date()).getFullYear();
  const years = Array.from(new Array(25), (val, index) => y-index);

  useEffect(() => {
    getMovies(year);
  }, [])

  useEffect(() => {
    getMovies(year);
  }, [year])

  // useEffect(() => {
  //   if(movies===undefined) return;

  //   movies.forEach((item) => {
  //     const id = uuid();
  //     item.id=id;
  //     setShowemovies([...movies])
  //   })
  // }, [movies])

const getMovies = async (year='') => {
  try{ 
    let moviesRequest; 
    if(search.length>2){
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=${search}&y=${year}&apikey=d0108237`
    )}
    else {
      moviesRequest = await axios.get(
      `http://www.omdbapi.com/?s=Iron&y=${year}&apikey=d0108237`
    )}
    console.log(moviesRequest.data.Search)
    setMovies(moviesRequest.data.Search)
  }
  catch (error){
    console.log(error)
  }
}

const filterYear = (e) => {
  setYear(e.target.value)
}
// useEffect(() => {
//   getMovies(page,year);
// },[page])



  return (
    <div>
      <input type="text" onChange={(e)=>{setSearch(e.target.value)}} />
      {/* <button onClick={(() => {getMovies(search)})}> Search </button> */}
      <button onClick={(() => {getMovies(year)})}> Search </button>
      <select onChange={filterYear}>
        <option value="" hidden >Year</option> 
        {
          years.map((year, index) => {
            return <option key={`year${index}`} value={year}>{year}</option>
          })
        }
      </select>

      <div className="movies-container">
        {movies==undefined? (
          <div> Movies not found</div>
        ) : (showmovies.length>0 ?(
          showmovies.map((m) => {
            return (
              <div>
                {m.Poster==="N/A"?(
                  <div> Poster not available</div>
                ):(
                  <img src={m.Poster} alt="" />
                )}
              </div>
            )
          })
          ):(''))}
      </div>
      {movies && movies.length > 0 ? movies.map(m => {
        return (
          <div>
            <img src={m.Poster} alt="" />
            <p>{m.Title}</p>
          </div>
        )
      }): ''}
    </div>
  )
}

// ReactDOM.render(<App/>, document.getElementById('root'));

import React,{useState, useEffect} from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import {AiFillLike} from 'react-icons/ai';
import {FcNext} from 'react-icons/fc';
import {IoIosArrowBack} from 'react-icons/io'
import { Link } from "react-router-dom";
// import 

    export default function layout() {
    const [movies, setMovies] = useState([]);
    const [search, setSearch] = useState("harry");
    const [page , setPage] = useState(0);
    const [isClicked, setIsClicked] = useState(Array(10).fill(false));

    const handleClick = (index) =>{
        // if(isClicked){
        //     setlikes(likes - 1);
        // }else{
        //     setlikes(likes + 1);
        // }
        setIsClicked(isClicked.map((c,i)=>{
            if(i==index){
                return !c;
            }
            else return c;
        }));
    }

    useEffect(()=>{
        console.log(isClicked);
        setMovies([...movies])
    },[isClicked])

    const getMovies = async () =>{
        if(search.length > 3){
            const moviesRequest = await axios.get(
                `http://www.omdbapi.com/?s=${search}&p=${page}&apikey=2a607171`
            );
            // con
            // console.log("hit");
            setMovies(moviesRequest.data.Search);
            console.log(moviesRequest);
        }else{
            alert("Search String should be > 3");
        }

    }

    useEffect(() =>{
        getMovies();
    },[]);

    return(
        <>
            <h1 style={{display:'flex', justifyContent:'center', background:'black',color:'white'}}>IMDB SEARCH APP</h1>
            <div style={{display:'flex', justifyContent:'center'}}>
            <input type="search"  placeholder="Search your movie Here" value={search} onChange = {(e) => setSearch(e.target.value)}></input>
            <button onClick={getMovies}></button>
            </div>
            <div style={{display:'flex',flexWrap:'wrap',justifyContent:'space-between'}}>
            {movies.length > 0 ? movies.map((movie,i) => {
                return(
                    <>
                    <div style={styles.card}>
                    <Link to={`/${movie.imdbID}`}>
                        <img src={movie.Poster}/>
                    </Link>
                        <div style={{display:'flex',justifyContent:'space-between', alignItems:'center'}}>
                       <div><p>{movie.Title}</p></div> 
                        <div><button onClick={()=>handleClick(i)}><AiFillLike />{isClicked[i]?'Unlike':'Like'}</button></div>
                        </div>
                    </div>    
                    </>

                )
            }): ''}
            </div>
            <div>
                <button><IoIosArrowBack/></button>
                <button><FcNext/></button>
            </div>
        </>
    )
}
const styles = {
    // card: {
        card: {
            display: "flex",
            flexDirection: "column",
            padding: "10px",
            marginBottom: "30px",
            justifyContent: "center",
            alignItems: "center",
            // backgroundColor: "green"
            boxShadow: "0px 1px 5px 5px rgb(0,0,0)"
        }
    // }
}
